    const startTime = Date.now();

    try {
      // Pre-initialization phase
      await executeHooks(name, LifecycleHook.BEFORE_INIT, { phase: LifecyclePhase.PRE_INIT });
      updateComponentState(component, ComponentState.INITIALIZING);

