      }

      updateComponentState(component, ComponentState.STOPPED);
      await executeHooks(name, LifecycleHook.AFTER_STOP, { phase: LifecyclePhase.POST_STOP });

      console.log(`â¹ï¸ Stopped ${name}`);

    } catch (error) {
