      state.stats.lastFpsUpdate = now;
    }
  };

  const getStats = () => ({
    ...state.stats,
    pipelineStats: getPipelineStats(),
    orchestratorStats: state.orchestrator ? state.orchestrator.getStats() : null,
    activePipelines: Array.from(state.pipelines.keys())
  });

