 * Image Color Processing Module
 * Handles color space conversions and format transformations
 */

import { IMAGE_FORMATS } from '../image-operations.js';
import { ErrorCategory, ErrorSeverity, handleError } from '../../../shared/utils/error-handler.js';

export const createColorProcessor = (resourcePool) => {
  /**
   * Convert color space between different formats
   */
  const convertColorSpace = (imageData, fromFormat, toFormat) => {
    try {
      if (fromFormat === toFormat) {
        return imageData;
      }

      const fromFormatInfo = IMAGE_FORMATS[fromFormat];
      const toFormatInfo = IMAGE_FORMATS[toFormat];

      if (!fromFormatInfo || !toFormatInfo) {
        throw new Error(`Unsupported format conversion: ${fromFormat} -> ${toFormat}`);
      }

      const sourceData = imageData.data;
      const pixelCount = imageData.width * imageData.height;
      const targetBuffer = resourcePool.getImageBuffer(
        imageData.width, 
        imageData.height, 
        toFormatInfo.channels
      );

      // Handle specific conversions
      if (fromFormat === 'RGBA') {
        return convertFromRGBA(sourceData, pixelCount, targetBuffer, toFormat);
      } else if (toFormat === 'RGBA') {
        return convertToRGBA(sourceData, pixelCount, targetBuffer, fromFormat, imageData.width, imageData.height);
      } else {
        // Convert through RGBA as intermediate format
        const rgbaData = convertToRGBA(sourceData, pixelCount, new Uint8ClampedArray(pixelCount * 4), fromFormat, imageData.width, imageData.height);
        return convertFromRGBA(rgbaData.data, pixelCount, targetBuffer, toFormat);
      }

    } catch (error) {
      handleError(
        `Color space conversion failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { fromFormat, toFormat }
      );
      throw error;
    }
  };

  /**
   * Convert from RGBA to other formats
   */
  const convertFromRGBA = (sourceData, pixelCount, targetBuffer, toFormat) => {
    switch (toFormat) {
      case 'RGB':
        for (let i = 0; i < pixelCount; i++) {
          const sourceIdx = i * 4;
          const targetIdx = i * 3;
          targetBuffer[targetIdx] = sourceData[sourceIdx];         // R
          targetBuffer[targetIdx + 1] = sourceData[sourceIdx + 1]; // G
          targetBuffer[targetIdx + 2] = sourceData[sourceIdx + 2]; // B
        }
        break;

      case 'BGR':
        for (let i = 0; i < pixelCount; i++) {
          const sourceIdx = i * 4;
          const targetIdx = i * 3;
          targetBuffer[targetIdx] = sourceData[sourceIdx + 2];     // B
          targetBuffer[targetIdx + 1] = sourceData[sourceIdx + 1]; // G
          targetBuffer[targetIdx + 2] = sourceData[sourceIdx];     // R
        }
        break;

      case 'GRAYSCALE':
        for (let i = 0; i < pixelCount; i++) {
          const sourceIdx = i * 4;
          const gray = Math.round(
            0.299 * sourceData[sourceIdx] +     // R
            0.587 * sourceData[sourceIdx + 1] + // G
            0.114 * sourceData[sourceIdx + 2]   // B
          );
          targetBuffer[i] = gray;
        }
        break;

      default:
        throw new Error(`Unsupported target format: ${toFormat}`);
    }

    return {
      data: targetBuffer,
      width: Math.sqrt(pixelCount * IMAGE_FORMATS[toFormat].channels / IMAGE_FORMATS[toFormat].channels),
      height: Math.sqrt(pixelCount * IMAGE_FORMATS[toFormat].channels / IMAGE_FORMATS[toFormat].channels),
      format: toFormat
    };
  };

  /**
   * Convert from other formats to RGBA
   */
  const convertToRGBA = (sourceData, pixelCount, targetBuffer, fromFormat, width, height) => {
    switch (fromFormat) {
      case 'RGB':
        for (let i = 0; i < pixelCount; i++) {
          const sourceIdx = i * 3;
          const targetIdx = i * 4;
          targetBuffer[targetIdx] = sourceData[sourceIdx];         // R
          targetBuffer[targetIdx + 1] = sourceData[sourceIdx + 1]; // G
          targetBuffer[targetIdx + 2] = sourceData[sourceIdx + 2]; // B
          targetBuffer[targetIdx + 3] = 255;                       // A
        }
        break;

      case 'BGR':
        for (let i = 0; i < pixelCount; i++) {
          const sourceIdx = i * 3;
          const targetIdx = i * 4;
          targetBuffer[targetIdx] = sourceData[sourceIdx + 2];     // R
          targetBuffer[targetIdx + 1] = sourceData[sourceIdx + 1]; // G
          targetBuffer[targetIdx + 2] = sourceData[sourceIdx];     // B
          targetBuffer[targetIdx + 3] = 255;                       // A
        }
        break;

      case 'GRAYSCALE':
        for (let i = 0; i < pixelCount; i++) {
          const targetIdx = i * 4;
          const grayValue = sourceData[i];
          targetBuffer[targetIdx] = grayValue;     // R
          targetBuffer[targetIdx + 1] = grayValue; // G
          targetBuffer[targetIdx + 2] = grayValue; // B
          targetBuffer[targetIdx + 3] = 255;       // A
        }
        break;

      default:
        throw new Error(`Unsupported source format: ${fromFormat}`);
    }

    return new ImageData(targetBuffer, width, height);
  };

  /**
   * Normalize image pixel values to 0-1 range
   */
  const normalizeImage = (imageData) => {
    try {
      const { data } = imageData;
      const normalizedData = new Float32Array(data.length);

      for (let i = 0; i < data.length; i++) {
        normalizedData[i] = data[i] / 255.0;
      }

      return {
        ...imageData,
        data: normalizedData,
        normalized: true
      };

    } catch (error) {
      handleError(
        `Image normalization failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR
      );
      throw error;
    }
  };

  /**
   * Denormalize image pixel values from 0-1 to 0-255 range
   */
  const denormalizeImage = (imageData) => {
    try {
      const { data } = imageData;
      const denormalizedData = new Uint8ClampedArray(data.length);

      for (let i = 0; i < data.length; i++) {
        denormalizedData[i] = Math.round(Math.max(0, Math.min(255, data[i] * 255)));
      }

      return {
        ...imageData,
        data: denormalizedData,
        normalized: false
      };

    } catch (error) {
      handleError(
        `Image denormalization failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR
      );
      throw error;
    }
  };

  /**
   * Apply gamma correction
   */
  const gammaCorrection = (imageData, gamma = 2.2) => {
    try {
      const { data } = imageData;
      const correctedData = new Uint8ClampedArray(data.length);
      const invGamma = 1.0 / gamma;

      // Pre-compute lookup table for efficiency
      const gammaTable = new Uint8Array(256);
      for (let i = 0; i < 256; i++) {
        gammaTable[i] = Math.round(255 * Math.pow(i / 255, invGamma));
      }

      for (let i = 0; i < data.length; i += 4) {
        correctedData[i] = gammaTable[data[i]];         // R
        correctedData[i + 1] = gammaTable[data[i + 1]]; // G
        correctedData[i + 2] = gammaTable[data[i + 2]]; // B
        correctedData[i + 3] = data[i + 3];             // A (unchanged)
      }

      return new ImageData(correctedData, imageData.width, imageData.height);

    } catch (error) {
      handleError(
        `Gamma correction failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { gamma }
      );
      throw error;
    }
  };

  /**
   * Adjust brightness and contrast
   */
  const adjustBrightnessContrast = (imageData, brightness = 0, contrast = 0) => {
    try {
      const { data } = imageData;
      const adjustedData = new Uint8ClampedArray(data.length);

      // Convert contrast from -100,100 range to multiplier
      const contrastFactor = (259 * (contrast + 255)) / (255 * (259 - contrast));

      for (let i = 0; i < data.length; i += 4) {
        // Apply brightness and contrast to RGB channels
        for (let j = 0; j < 3; j++) {
          let pixel = data[i + j];

          // Apply contrast
          pixel = contrastFactor * (pixel - 128) + 128;

          // Apply brightness
          pixel += brightness;

          // Clamp to valid range
          adjustedData[i + j] = Math.max(0, Math.min(255, pixel));
        }

        // Keep alpha unchanged
        adjustedData[i + 3] = data[i + 3];
      }

      return new ImageData(adjustedData, imageData.width, imageData.height);

    } catch (error) {
      handleError(
        `Brightness/contrast adjustment failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { brightness, contrast }
      );
      throw error;
    }
  };

  return {
    convertColorSpace,
    normalizeImage,
    denormalizeImage,
    gammaCorrection,
    adjustBrightnessContrast,

    // Export individual conversion functions
    convertFromRGBA,
    convertToRGBA
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/core/engine/processors/crop-processor.js","messages":[{"ruleId":"max-lines-per-function","severity":1,"message":"Arrow function has too many lines (227). Maximum allowed is 150.","line":8,"column":36,"nodeType":"ArrowFunctionExpression","messageId":"exceed","endLine":313,"endColumn":2}],"suppressedMessages":[],"errorCount":0,"fatalErrorCount":0,"warningCount":1,"fixableErrorCount":0,"fixableWarningCount":0,"source":"/**
 * Image Crop Processing Module
 * Handles image cropping and extraction operations
 */

import { ErrorCategory, ErrorSeverity, handleError } from '../../../shared/utils/error-handler.js';

export const createCropProcessor = (resourcePool) => {
  /**
   * Basic crop operation
   */
  const cropImage = (imageData, x, y, width, height) => {
    try {
      if (!imageData || !imageData.data) {
        throw new Error('Invalid image data provided');
      }

      const sourceWidth = imageData.width;
      const sourceHeight = imageData.height;

      // Validate crop bounds
      if (x < 0 || y < 0 || x + width > sourceWidth || y + height > sourceHeight) {
        throw new Error('Crop bounds exceed image dimensions');
      }

      if (width <= 0 || height <= 0) {
        throw new Error('Crop dimensions must be positive');
      }

      const canvas = resourcePool.getCanvas(width, height);
      const ctx = canvas.getContext('2d');

      // Create source canvas
      const sourceCanvas = resourcePool.getCanvas(sourceWidth, sourceHeight);
      const sourceCtx = sourceCanvas.getContext('2d');
      sourceCtx.putImageData(imageData, 0, 0);

      // Draw cropped region
      ctx.drawImage(
        sourceCanvas,
        x, y, width, height,
        0, 0, width, height
      );

      const croppedData = ctx.getImageData(0, 0, width, height);

      // Clean up resources
      resourcePool.returnCanvas(canvas);
      resourcePool.returnCanvas(sourceCanvas);

      return croppedData;

    } catch (error) {
      handleError(
        `Image crop failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { 
          sourceSize: [imageData?.width, imageData?.height], 
          cropBounds: [x, y, width, height] 
        }
      );
      throw error;
    }
  };

  /**
   * Center crop to specific dimensions
   */
  const centerCropImage = (imageData, targetWidth, targetHeight) => {
    try {
      const sourceWidth = imageData.width;
      const sourceHeight = imageData.height;

      // Calculate crop area
      const cropWidth = Math.min(sourceWidth, targetWidth);
      const cropHeight = Math.min(sourceHeight, targetHeight);
      const cropX = Math.floor((sourceWidth - cropWidth) / 2);
      const cropY = Math.floor((sourceHeight - cropHeight) / 2);

      return cropImage(imageData, cropX, cropY, cropWidth, cropHeight);

    } catch (error) {
      handleError(
        `Image center cropping failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { 
          sourceSize: [imageData?.width, imageData?.height], 
          targetSize: [targetWidth, targetHeight] 
        }
      );
      throw error;
    }
  };

  /**
   * Smart crop to maintain aspect ratio
   */
  const aspectRatioCrop = (imageData, targetAspectRatio) => {
    try {
      const sourceWidth = imageData.width;
      const sourceHeight = imageData.height;
      const sourceAspectRatio = sourceWidth / sourceHeight;

      let cropWidth, cropHeight, cropX, cropY;

      if (sourceAspectRatio > targetAspectRatio) {
        // Source is wider - crop width
        cropHeight = sourceHeight;
        cropWidth = Math.round(sourceHeight * targetAspectRatio);
        cropX = Math.round((sourceWidth - cropWidth) / 2);
        cropY = 0;
      } else {
        // Source is taller - crop height
        cropWidth = sourceWidth;
        cropHeight = Math.round(sourceWidth / targetAspectRatio);
        cropX = 0;
        cropY = Math.round((sourceHeight - cropHeight) / 2);
      }

      return cropImage(imageData, cropX, cropY, cropWidth, cropHeight);

    } catch (error) {
      handleError(
        `Aspect ratio crop failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { targetAspectRatio }
      );
      throw error;
    }
  };

  /**
   * Crop with padding if needed
   */
  const cropWithPadding = (imageData, x, y, width, height, paddingColor = [0, 0, 0, 255]) => {
    try {
      const sourceWidth = imageData.width;
      const sourceHeight = imageData.height;

      // Calculate intersection with source image
      const sourceX = Math.max(0, x);
      const sourceY = Math.max(0, y);
      const sourceEndX = Math.min(sourceWidth, x + width);
      const sourceEndY = Math.min(sourceHeight, y + height);

      const intersectWidth = Math.max(0, sourceEndX - sourceX);
      const intersectHeight = Math.max(0, sourceEndY - sourceY);

      // Create output canvas
      const canvas = resourcePool.getCanvas(width, height);
      const ctx = canvas.getContext('2d');

      // Fill with padding color
      ctx.fillStyle = `rgba(${paddingColor[0]}, ${paddingColor[1]}, ${paddingColor[2]}, ${paddingColor[3]/255})`;
      ctx.fillRect(0, 0, width, height);

      if (intersectWidth > 0 && intersectHeight > 0) {
        // Create source canvas
        const sourceCanvas = resourcePool.getCanvas(sourceWidth, sourceHeight);
        const sourceCtx = sourceCanvas.getContext('2d');
        sourceCtx.putImageData(imageData, 0, 0);

        // Draw intersecting region
        const destX = sourceX - x;
        const destY = sourceY - y;

        ctx.drawImage(
          sourceCanvas,
          sourceX, sourceY, intersectWidth, intersectHeight,
          destX, destY, intersectWidth, intersectHeight
        );

        resourcePool.returnCanvas(sourceCanvas);
      }

      const result = ctx.getImageData(0, 0, width, height);
      resourcePool.returnCanvas(canvas);

      return result;

    } catch (error) {
      handleError(
        `Padded crop failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { cropBounds: [x, y, width, height] }
      );
      throw error;
    }
  };

  /**
   * Multi-region crop (extract multiple regions at once)
   */
  const multiCrop = (imageData, regions) => {
    try {
      if (!Array.isArray(regions) || regions.length === 0) {
        throw new Error('Regions must be a non-empty array');
      }

      return regions.map((region, index) => {
        const { x, y, width, height, id } = region;

        try {
          const cropped = cropImage(imageData, x, y, width, height);
          return {
            id: id || `region_${index}`,
            imageData: cropped,
            bounds: { x, y, width, height },
            success: true
          };
        } catch (error) {
          return {
            id: id || `region_${index}`,
            imageData: null,
            bounds: { x, y, width, height },
            success: false,
            error: error.message
          };
        }
      });

    } catch (error) {
      handleError(
        `Multi-crop failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { regionCount: regions?.length }
      );
      throw error;
    }
  };

  /**
   * Extract region of interest (ROI) with metadata
   */
  const extractROI = (imageData, roi) => {
    try {
      const { x, y, width, height, label, confidence } = roi;
      const cropped = cropImage(imageData, x, y, width, height);

      return {
        imageData: cropped,
        metadata: {
          originalBounds: { x, y, width, height },
          label: label || 'unknown',
          confidence: confidence || 1.0,
          extractedAt: Date.now(),
          originalSize: {
            width: imageData.width,
            height: imageData.height
          }
        }
      };

    } catch (error) {
      handleError(
        `ROI extraction failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { roi }
      );
      throw error;
    }
  };

  /**
   * Normalize bounding box coordinates
   */
  const normalizeBoundingBox = (bbox, imageWidth, imageHeight) => {
    try {
      let { x, y, width, height } = bbox;

      // Handle relative coordinates (0-1 range)
      if (x <= 1 && y <= 1 && width <= 1 && height <= 1) {
        x = Math.round(x * imageWidth);
        y = Math.round(y * imageHeight);
        width = Math.round(width * imageWidth);
        height = Math.round(height * imageHeight);
      }

      // Clamp to image bounds
      x = Math.max(0, Math.min(imageWidth - 1, x));
      y = Math.max(0, Math.min(imageHeight - 1, y));
      width = Math.max(1, Math.min(imageWidth - x, width));
      height = Math.max(1, Math.min(imageHeight - y, height));

      return { x, y, width, height };

    } catch (error) {
      handleError(
        `Bounding box normalization failed: ${error.message}`,
        ErrorCategory.PROCESSING,
        ErrorSeverity.ERROR,
        { bbox, imageSize: [imageWidth, imageHeight] }
      );
      throw error;
    }
  };

  return {
    cropImage,
    centerCropImage,
    aspectRatioCrop,
    cropWithPadding,
    multiCrop,
    extractROI,
    normalizeBoundingBox
