  const releaseObject = (obj) => {
    if (obj && obj.type) {
      return state.memoryPool.release(obj.type, obj);
    }
    console.warn('Attempting to release untracked object');
    return false;
  };

  // Get comprehensive statistics
  const getStats = () => ({
    ...state.stats,
    memoryPool: state.memoryPool.getStats(),
    buffers: Object.fromEntries(
      Array.from(state.buffers.entries()).map(([id, buffer]) => [
        id,
        buffer.getStats()
      ])
    ),
    thresholds: { ...state.thresholds },
    currentMemoryUsage: monitorMemoryUsage()
  });

  // Configuration updates
  const updateThresholds = (newThresholds) => {
    Object.assign(state.thresholds, newThresholds);
  };

  // Initialize memory optimizer
  initializePools();

  return {
    // Pool management
    createGazeData,
    createVideoFrame,
    createBufferEntry,
    releaseObject,

    // Buffer management
    createOptimizedBuffer,
    getBuffer: (id) => state.buffers.get(id),

    // Monitoring and optimization
    startMonitoring,
    stopMonitoring,
    monitorMemoryUsage,
    applyMemoryOptimization,
    scheduleGC,

    // Statistics and configuration
    getStats,
    updateThresholds,

    // Cleanup
    cleanup: () => {
      stopMonitoring();
      if (state.memoryPool && typeof state.memoryPool.clear === 'function') {
        state.memoryPool.clear();
      }
      for (const buffer of state.buffers.values()) {
        if (buffer && typeof buffer.clear === 'function') {
          buffer.clear();
        }
      }
      state.buffers.clear();
    }
  };
};

