      config: { ...state.config },
      initialized: state.isInitialized,
      performance: {
        fps: 30,
        latency: '25-40ms',
        modelSize: '3MB',
        cpuUsage: 'medium',
        memoryUsage: 'low'
      },
      capabilities: ['iris-tracking', 'gaze-estimation', 'eye-tracking']
