export {
  createDeviceDiscovery,
  createEyeTrackerDevice,
  createGazeProcessor,
  createEyeTrackingStreaming,
  createEyeTrackingSystem
};

// Default API instance factory
export const createEyeTracker = (config = {}) => {
  return createEyeTrackingAPI(config);
