    try {
      const wasStreaming = state.isStreaming;

      if (wasStreaming) {
        await stopStreaming();
      }

      await initializeMediaCapture(newQuality);

      if (wasStreaming) {
        await startStreaming();
      }

      // Notify callbacks
      state.callbacks.onQualityChange.forEach(callback => {
        try {
          callback({ oldQuality: state.quality, newQuality });
        } catch (error) {
