    for (let k = 0; k < N / 2; k++) {
      let sumReal = 0;
      let sumImag = 0;

      for (let n = 0; n < N; n++) {
        const angle = -2 * Math.PI * k * n / N;
        sumReal += audioBuffer[n] * Math.cos(angle);
        sumImag += audioBuffer[n] * Math.sin(angle);
