    processFrame,
    getSegments,
    getStats: () => ({ ...state.stats }),
    updateConfig: (newConfig) => Object.assign(state.config, newConfig),
    reset: () => {
      state.featureHistory = [];
      state.currentSegment = null;
      state.segments = [];
      state.stats = {
        totalSegments: 0,
        averageSegmentDuration: 0,
        changePointsDetected: 0
      };
    }
  };
