    if (!fp1 || !fp2) return 0;

    let totalDistance = 0;
    let components = 0;

    // Mean similarity (using cosine similarity)
    let dot = 0, norm1 = 0, norm2 = 0;
    for (let i = 0; i < state.config.mfccCoefficients; i++) {
