 * Spectral Processing for Noise Reduction
 * Handles noise profile management and spectral subtraction
 */

export const createSpectralProcessor = (state) => {
  // Update noise profile during quiet segments
  const updateNoiseProfile = (magnitudes) => {
    if (!state.noiseProfileInitialized) {
      state.noiseProfile = new Float32Array(magnitudes.length);
      for (let i = 0; i < magnitudes.length; i++) {
        state.noiseProfile[i] = magnitudes[i];
      }
      state.noiseProfileInitialized = true;
    } else {
      // Exponential moving average
      const rate = state.config.learningRate;
      for (let i = 0; i < magnitudes.length; i++) {
        state.noiseProfile[i] = rate * state.noiseProfile[i] + (1 - rate) * magnitudes[i];
      }
    }

    state.adaptationFrames++;
  };

  // Apply spectral subtraction
  const applySpectralSubtraction = (magnitudes) => {
    if (!state.noiseProfile) {
      return magnitudes;
    }

    const enhancedMagnitudes = new Float32Array(magnitudes.length);

    for (let i = 0; i < magnitudes.length; i++) {
      const signalMag = magnitudes[i];
      const noiseMag = state.noiseProfile[i];

      // Spectral subtraction
      const subtractedMag = signalMag - state.config.alpha * noiseMag;
      const flooredMag = Math.max(subtractedMag, state.config.beta * signalMag);

      // Apply gain limits
      const gain = signalMag > 0 ? flooredMag / signalMag : 1;
      const limitedGain = Math.max(state.config.minGain, Math.min(state.config.maxGain, gain));

      enhancedMagnitudes[i] = signalMag * limitedGain;
    }

    return enhancedMagnitudes;
  };

  // Update statistics
  const updateStats = () => {
    state.stats.totalFrames++;
    if (state.noiseProfile) {
      state.stats.noiseReductionApplied++;
      const avgNoise = state.noiseProfile.reduce((sum, n) => sum + n, 0) / state.noiseProfile.length;
      state.stats.averageNoiseLevel = avgNoise;
    }
  };

  return {
    updateNoiseProfile,
    applySpectralSubtraction,
    updateStats
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/features/speech-analysis/audio/noise-reduction-window.js","messages":[],"suppressedMessages":[],"errorCount":0,"fatalErrorCount":0,"warningCount":0,"fixableErrorCount":0,"fixableWarningCount":0,"usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","