 * Audio Quality Analysis Core
 * Core quality analysis and scoring logic
 */

export const createQualityAnalysisCore = (state) => {
  // Run individual quality analyses
  const runAnalyses = (audioBuffer, fftMagnitudes, isQuiet) => {
    const snrResult = state.snrCalculator.calculateSNR(audioBuffer, fftMagnitudes, isQuiet);
    let thdResult = { thd: 0, quality: 100 };
    if (fftMagnitudes) {
      thdResult = state.thdCalculator.calculateTHD(fftMagnitudes);
    }
    const clippingResult = state.clippingDetector.detectClipping(audioBuffer);

    return { snrResult, thdResult, clippingResult };
  };

  // Calculate weighted overall quality score
  const calculateOverallQuality = (snrResult, thdResult, clippingResult) => {
    const snrWeight = 0.4;
    const thdWeight = 0.3;
    const clippingWeight = 0.3;

    return (snrResult.quality * snrWeight) +
           (thdResult.quality * thdWeight) +
           (clippingResult.quality * clippingWeight);
  };

  // Determine quality level from score
  const determineQualityLevel = (overallQuality) => {
    if (overallQuality >= state.config.qualityThresholds.excellent) {
      return 'excellent';
    } else if (overallQuality >= state.config.qualityThresholds.good) {
      return 'good';
    } else if (overallQuality >= state.config.qualityThresholds.fair) {
      return 'fair';
    } else {
      return 'poor';
    }
  };

  // Generate quality recommendations
  const generateRecommendations = (snrResult, thdResult, clippingResult) => {
    const recommendations = [];

    if (snrResult.snr < 10) {
      recommendations.push('Reduce background noise or increase microphone gain');
    }
    if (thdResult.thdPercent > 10) {
      recommendations.push('Check for audio hardware distortion or overdriving');
    }
    if (clippingResult.isClipped) {
      recommendations.push('Reduce input level to prevent audio clipping');
    }
    if (snrResult.snr > 40 && thdResult.thdPercent < 1 && !clippingResult.isClipped) {
      recommendations.push('Excellent audio quality - maintain current settings');
    } else if (snrResult.signalStrength < -20) {
      recommendations.push('Increase microphone sensitivity or move closer to source');
    }

    return recommendations;
  };

  // Calculate detailed metrics
  const calculateDetailedMetrics = (snrResult, thdResult, clippingResult, audioBuffer) => {
    const rmsLevel = Math.sqrt(audioBuffer.reduce((sum, s) => sum + s * s, 0) / audioBuffer.length);
    const peakLevel = Math.max(...audioBuffer.map(Math.abs));

    return {
      signalStrength: 20 * Math.log10(rmsLevel + 1e-10),
      noiseLevel: snrResult.noiseLevel || -60,
      distortion: thdResult.thdPercent || 0,
      clarity: Math.max(0, 100 - (thdResult.thdPercent || 0) - Math.max(0, (clippingResult.clippingPercent || 0) * 10)),
      dynamicRange: 20 * Math.log10((peakLevel + 1e-10) / (rmsLevel + 1e-10)),
      peakLevel: 20 * Math.log10(peakLevel + 1e-10)
    };
  };

  return {
    runAnalyses,
    calculateOverallQuality,
    determineQualityLevel,
    generateRecommendations,
    calculateDetailedMetrics
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/features/speech-analysis/audio/quality-analyzer-stats.js","messages":[{"ruleId":"no-mixed-operators","severity":1,"message":"Unexpected mix of '*' and '+'. Use parentheses to clarify the intended order of operations.","line":16,"column":42,"nodeType":"BinaryExpression","messageId":"unexpectedMixedOperator","endLine":16,"endColumn":43},{"ruleId":"no-mixed-operators","severity":1,"message":"Unexpected mix of '*' and '+'. Use parentheses to clarify the intended order of operations.","line":16,"column":59,"nodeType":"BinaryExpression","messageId":"unexpectedMixedOperator","endLine":16,"endColumn":60},{"ruleId":"no-mixed-operators","severity":1,"message":"Unexpected mix of '+' and '*'. Use parentheses to clarify the intended order of operations.","line":16,"column":59,"nodeType":"BinaryExpression","messageId":"unexpectedMixedOperator","endLine":16,"endColumn":60},{"ruleId":"no-mixed-operators","severity":1,"message":"Unexpected mix of '+' and '*'. Use parentheses to clarify the intended order of operations.","line":16,"column":73,"nodeType":"BinaryExpression","messageId":"unexpectedMixedOperator","endLine":16,"endColumn":74}],"suppressedMessages":[],"errorCount":0,"fatalErrorCount":0,"warningCount":4,"fixableErrorCount":0,"fixableWarningCount":0,"source":"/**
 * Quality Analysis Statistics Manager
 * Manages quality statistics and trends
 */

export const createQualityStatsManager = (state) => {
  // Update quality statistics
  const updateStats = (overallQuality, qualityLevel) => {
    state.stats.totalFrames++;

    // Update running average
    const alpha = 0.1; // Smoothing factor
    if (state.stats.totalFrames === 1) {
      state.stats.overallQuality = overallQuality;
    } else {
      state.stats.overallQuality = alpha * overallQuality + (1 - alpha) * state.stats.overallQuality;
    }

    // Update quality distribution
    state.stats.qualityDistribution[qualityLevel]++;

    // Add to quality trend (keep last 100 samples)
    state.stats.qualityTrend.push({
      quality: overallQuality,
      level: qualityLevel,
      timestamp: Date.now()
    });

    if (state.stats.qualityTrend.length > 100) {
      state.stats.qualityTrend.shift();
    }
  };

  // Get quality trend analysis
  const getTrendAnalysis = () => {
    if (state.stats.qualityTrend.length < 5) {
      return { trend: 'insufficient_data', confidence: 0 };
    }

    const recent = state.stats.qualityTrend.slice(-10);
    const older = state.stats.qualityTrend.slice(-20, -10);

    if (older.length === 0) {
      return { trend: 'stable', confidence: 0.5 };
    }

    const recentAvg = recent.reduce((sum, s) => sum + s.quality, 0) / recent.length;
    const olderAvg = older.reduce((sum, s) => sum + s.quality, 0) / older.length;

    const change = recentAvg - olderAvg;
    const confidence = Math.min(1.0, Math.abs(change) / 10);

    let trend;
    if (Math.abs(change) < 2) {
      trend = 'stable';
    } else if (change > 0) {
      trend = change > 5 ? 'improving_significantly' : 'improving';
    } else {
      trend = change < -5 ? 'degrading_significantly' : 'degrading';
    }

    return { trend, confidence, change };
  };

  // Get comprehensive statistics
  const getComprehensiveStats = () => ({
    ...state.stats,
    qualityTrendAnalysis: getTrendAnalysis(),
    averageQualityByLevel: {
      excellent: state.stats.qualityDistribution.excellent / Math.max(1, state.stats.totalFrames) * 100,
      good: state.stats.qualityDistribution.good / Math.max(1, state.stats.totalFrames) * 100,
      fair: state.stats.qualityDistribution.fair / Math.max(1, state.stats.totalFrames) * 100,
      poor: state.stats.qualityDistribution.poor / Math.max(1, state.stats.totalFrames) * 100
    }
  });

  return {
    updateStats,
    getTrendAnalysis,
    getComprehensiveStats
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/features/speech-analysis/audio/realtime-audio-analyzer.js","messages":[{"ruleId":"max-lines-per-function","severity":1,"message":"Arrow function has too many lines (184). Maximum allowed is 150.","line":14,"column":44,"nodeType":"ArrowFunctionExpression","messageId":"exceed","endLine":273,"endColumn":2}],"suppressedMessages":[],"errorCount":0,"fatalErrorCount":0,"warningCount":1,"fixableErrorCount":0,"fixableWarningCount":0,"source":"/**
 * Real-time Audio Analysis Pipeline
 * Advanced audio processing and feature extraction for speech analysis
 * Following functional programming patterns with factory functions
 */

// Import modular components
import { createAudioProcessingChain } from './processing-chain.js';
import { createFeatureExtractor } from './feature-extractor.js';
import { createAnalysisProcessor } from './analysis-processor.js';
import { createCallbackNotifier } from './callback-notifier.js';

// Create real-time audio analyzer factory
export const createRealtimeAudioAnalyzer = (config = {}) => {
  const state = {
    audioContext: null,
    analyser: null,
    microphone: null,
    scriptProcessor: null,

    // Analysis buffers
    buffers: {
      time: null,
      frequency: null,
      fftSize: config.fftSize || 2048,
      smoothing: config.smoothingTimeConstant || 0.8
    },

    // Feature extraction
    features: {
      pitch: 0,
      energy: 0,
      spectralCentroid: 0,
      spectralRolloff: 0,
      zeroCrossingRate: 0,
      mfcc: [],
      formants: []
    },

    // Configuration
    config: {
      sampleRate: config.sampleRate || 48000,
      fftSize: config.fftSize || 2048,
      smoothingTimeConstant: config.smoothingTimeConstant || 0.8,
      minPitch: config.minPitch || 50, // Hz
      maxPitch: config.maxPitch || 400, // Hz
      frameSize: config.frameSize || 512,
      hopSize: config.hopSize || 256,
      enablePreprocessing: config.enablePreprocessing !== false,
      enableNoiseSuppression: config.enableNoiseSuppression !== false,
      ...config
    },

    // State tracking
    isInitialized: false,
    isAnalyzing: false,

    // Callbacks
    callbacks: {
      onFeatures: [],
      onPitch: [],
      onEnergy: [],
      onSpectrum: [],
      onError: []
    },

    // Performance metrics
    metrics: {
      framesProcessed: 0,
      lastProcessTime: 0,
      averageLatency: 0
    }
  };

  // Create modular components
  const callbackNotifier = createCallbackNotifier(state);
  const featureExtractor = createFeatureExtractor(state.config);
  const analysisProcessor = createAnalysisProcessor(state, featureExtractor, callbackNotifier);
  let processingChain = null;

  // Initialize audio context and analyzer
  const initialize = async () => {
    try {
      // Create audio context
      const AudioContext = window.AudioContext || window.webkitAudioContext;
      state.audioContext = new AudioContext({
        sampleRate: state.config.sampleRate,
        latencyHint: 'interactive'
      });

      // Create analyser node
      state.analyser = state.audioContext.createAnalyser();
      state.analyser.fftSize = state.config.fftSize;
      state.analyser.smoothingTimeConstant = state.config.smoothingTimeConstant;

      // Initialize buffers
      state.buffers.time = new Float32Array(state.analyser.fftSize);
      state.buffers.frequency = new Float32Array(state.analyser.frequencyBinCount);

      // Create processing chain
      processingChain = createAudioProcessingChain(state.audioContext, state.config);

      // Create preprocessing chain if enabled
      if (state.config.enablePreprocessing) {
        processingChain.createPreprocessingChain();
      }

      state.isInitialized = true;
      console.log('âœ… Real-time audio analyzer initialized');
      console.log(`ðŸ“Š Sample rate: ${state.audioContext.sampleRate}Hz`);
      console.log(`ðŸ“Š FFT size: ${state.analyser.fftSize}`);

      return true;
    } catch (error) {
      console.error('Failed to initialize audio analyzer:', error);
      callbackNotifier.notifyError(error);
      throw error;
    }
  };

  // Start audio analysis
  const startAnalysis = async (stream) => {
    if (!state.isInitialized) {
      await initialize();
    }

    if (state.isAnalyzing) {
      console.warn('Already analyzing audio');
      return;
    }

    try {
      // Create microphone source
      state.microphone = state.audioContext.createMediaStreamSource(stream);

      // Create script processor for real-time processing
      state.scriptProcessor = state.audioContext.createScriptProcessor(
        state.config.frameSize,
        1, // mono input
        1  // mono output
      );

      // Setup audio processing callback
      state.scriptProcessor.onaudioprocess = analysisProcessor.processAudioFrame;

      // Connect audio graph
      if (state.config.enablePreprocessing) {
        processingChain.connectPreprocessingChain(state.microphone, state.analyser, state.scriptProcessor);
      } else {
        processingChain.connectSimpleChain(state.microphone, state.analyser, state.scriptProcessor);
      }

      state.scriptProcessor.connect(state.audioContext.destination);

      state.isAnalyzing = true;
      console.log('ðŸŽ¤ Started real-time audio analysis');

    } catch (error) {
      console.error('Failed to start audio analysis:', error);
      callbackNotifier.notifyError(error);
      throw error;
    }
  };

  // Stop audio analysis
  const stopAnalysis = () => {
    if (!state.isAnalyzing) return;

    try {
      if (state.scriptProcessor) {
        state.scriptProcessor.disconnect();
        state.scriptProcessor.onaudioprocess = null;
        state.scriptProcessor = null;
      }

      if (state.microphone) {
        state.microphone.disconnect();
        state.microphone = null;
      }

      // Cleanup processing chain
      if (processingChain) {
        processingChain.cleanup();
      }

      state.isAnalyzing = false;
      console.log('ðŸ›‘ Stopped real-time audio analysis');

    } catch (error) {
      console.error('Error stopping audio analysis:', error);
      callbackNotifier.notifyError(error);
    }
  };

  // Data access methods
  const getFeatures = () => ({ ...state.features });

  const getSpectrum = () => new Float32Array(state.buffers.frequency);

  const getWaveform = () => new Float32Array(state.buffers.time);

  const getMetrics = () => analysisProcessor.getProcessingStats();

  // Configuration methods
  const updateConfig = (newConfig) => {
    Object.assign(state.config, newConfig);

    if (state.analyser) {
      if (newConfig.fftSize) {
        state.analyser.fftSize = newConfig.fftSize;
        state.buffers.time = new Float32Array(state.analyser.fftSize);
        state.buffers.frequency = new Float32Array(state.analyser.frequencyBinCount);
      }

      if (newConfig.smoothingTimeConstant !== undefined) {
        state.analyser.smoothingTimeConstant = newConfig.smoothingTimeConstant;
      }
    }

    // Update processing chain parameters if needed
    if (processingChain && newConfig.gain !== undefined) {
      processingChain.updateParameters({ gain: newConfig.gain });
    }
  };

  // Cleanup
  const cleanup = () => {
    stopAnalysis();

    if (state.audioContext) {
      state.audioContext.close();
      state.audioContext = null;
    }

    if (processingChain) {
      processingChain.cleanup();
    }

    callbackNotifier.clearAllCallbacks();

    state.isInitialized = false;
    console.log('ðŸ§¹ Audio analyzer cleaned up');
  };

  return {
    // Core functionality
    initialize,
    startAnalysis,
    stopAnalysis,
    cleanup,

    // Data access
    getFeatures,
    getSpectrum,
    getWaveform,
    getMetrics,

    // Configuration
    updateConfig,
    getConfig: () => ({ ...state.config }),

    // Status
    isInitialized: () => state.isInitialized,
    isAnalyzing: () => state.isAnalyzing,

    // Event handlers - delegated to callbackNotifier
    onFeatures: callbackNotifier.onFeatures,
    onPitch: callbackNotifier.onPitch,
    onEnergy: callbackNotifier.onEnergy,
    onSpectrum: callbackNotifier.onSpectrum,
    onError: callbackNotifier.onError
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/features/speech-analysis/audio/speaker-diarization.js","messages":[{"ruleId":"no-unused-vars","severity":2,"message":"'createVoiceFingerprinting' is defined but never used. Allowed unused vars must match /^_/u.","line":13,"column":10,"nodeType":"Identifier","messageId":"unusedVar","endLine":13,"endColumn":35,"suggestions":[{"messageId":"removeVar","data":{"varName":"createVoiceFingerprinting"},"fix":{"range":[500,582],"text":""},"desc":"Remove unused variable 'createVoiceFingerprinting'."}]},{"ruleId":"no-unused-vars","severity":2,"message":"'createSpeakerChangeDetection' is defined but never used. Allowed unused vars must match /^_/u.","line":14,"column":10,"nodeType":"Identifier","messageId":"unusedVar","endLine":14,"endColumn":38,"suggestions":[{"messageId":"removeVar","