    initializeMetrics,
    updateWordMetrics,
    updateProcessingTime,
    updateErrorMetrics,
    updateBackendUsage,
    updateLanguageDistribution,
    updateAudioMetrics,
    startSession,
    endSession,
    generateMetricsReport,
    resetMetrics,
    exportMetrics
  };
