  const resetStatistics = () => {
    state.stats = {
      totalRequests: 0,
      preflightRequests: 0,
      blockedRequests: 0,
      allowedOrigins: new Map(),
      requestsByMethod: new Map()
    };
    console.log('ðŸ“Š CORS statistics reset');
  };

  /**
