  };

  return {
    // Core middleware function
    corsMiddleware,

    // Header utilities
    createCORSHeaders,
    applyCORSHeaders,
    handlePreflight,

    // Configuration management
    updateConfig,
    addAllowedOrigin,
    removeAllowedOrigin,

    // Monitoring
