    }

    const logData = {
      timestamp: new Date().toISOString(),
      requestId: errorResponse.requestId,
      error: {
        name: error.name,
        message: error.message,
        code: error.code,
        stack: error.stack
      },
      request: {
        method: request ? request.method : 'unknown',
        url: request ? request.url : 'unknown',
