 * Distribution System Routes Module
 * Handles data distribution, streaming, and client management
 */

export const createDistributionRoutes = ({
  getDistributionSystem,
  getDistributionOverallStatus,
  createDistributionStream,
  distributionStreams,
  distributionClients,
  middlewareSystem,
  createJSONResponse,
  createErrorResponse,
  generateSecureId
}) => {
  const routes = [];

  // Distribution status
  routes.push(['GET', '^/api/distribution/status$', async (request) => {
    try {

      const status = getDistributionOverallStatus();
      return createJSONResponse(status, 200);
    } catch (error) {

      return createErrorResponse(`Distribution status error: ${error.message}`, 500);
    }
  }]);

  // Service discovery
  routes.push(['GET', '^/api/distribution/discovery$', async (request) => {
    try {

      return createJSONResponse({
        services: ['udp', 'websocket', 'mqtt', 'sse'],
        timestamp: Date.now()
      }, 200);
    } catch (error) {

      return createErrorResponse(`Discovery error: ${error.message}`, 500);
    }
  }]);

  // Create new distribution stream
  routes.push(['POST', '^/api/distribution/streams$', async (request) => {
    try {
      const body = await request.json();


      const stream = await createDistributionStream(body);
      const host = request.headers.get('host') || 'localhost:3000';

      return createJSONResponse({
        stream_id: stream.id,
        data: stream,
        websocket_status_url: `ws://${host}/ws/distribution/events?stream=${stream.id}`,
        message: 'Distribution stream created successfully'
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to create stream: ${error.message}`, 400);
    }
  }]);

  // Get all distribution streams
  routes.push(['GET', '^/api/distribution/streams$', async (request) => {
    try {

      const streams = Array.from(distributionStreams.values());

      return createJSONResponse({
        streams,
        total: streams.length,
        active: streams.filter(s => s.status === 'active').length
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to get streams: ${error.message}`, 500);
    }
  }]);

  // Get specific distribution stream
  routes.push(['GET', '^/api/distribution/streams/([^/]+)$', async (request, params) => {
    try {
      const streamId = params[0];


      const stream = distributionStreams.get(streamId);
      if (!stream) {
        return createErrorResponse('Stream not found', 404);
      }

      return createJSONResponse({
        stream,
        clients: Array.from(distributionClients.values()).filter(c => c.streams.includes(streamId))
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to get stream: ${error.message}`, 500);
    }
  }]);

  // Update distribution stream
  routes.push(['PUT', '^/api/distribution/streams/([^/]+)$', async (request, params) => {
    try {
      const streamId = params[0];
      const body = await request.json();


      const stream = distributionStreams.get(streamId);
      if (!stream) {
        return createErrorResponse('Stream not found', 404);
      }

      // Update stream configuration
      Object.assign(stream.config, body);
      stream.updated_at = Date.now();

      return createJSONResponse({
        stream,
        message: 'Stream updated successfully'
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to update stream: ${error.message}`, 400);
    }
  }]);

  // Delete distribution stream
  routes.push(['DELETE', '^/api/distribution/streams/([^/]+)$', async (request, params) => {
    try {
      const streamId = params[0];


      const stream = distributionStreams.get(streamId);
      if (!stream) {
        return createErrorResponse('Stream not found', 404);
      }

      // Stop stream and remove
      stream.status = 'stopped';
      distributionStreams.delete(streamId);

      // Remove from clients
      for (const client of distributionClients.values()) {
        const index = client.streams.indexOf(streamId);
        if (index > -1) {
          client.streams.splice(index, 1);
        }
      }

      return createJSONResponse({
        message: 'Stream deleted successfully'
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to delete stream: ${error.message}`, 500);
    }
  }]);

  // Register distribution client
  routes.push(['POST', '^/api/distribution/clients$', async (request) => {
    try {
      const body = await request.json();


      const clientId = generateSecureId('client');
      const client = {
        id: clientId,
        ...body,
        streams: body.streams || [],
        registered_at: Date.now(),
        last_seen: Date.now(),
        status: 'active'
      };

      distributionClients.set(clientId, client);

      return createJSONResponse({
        client_id: clientId,
        client,
        message: 'Client registered successfully'
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to register client: ${error.message}`, 400);
    }
  }]);

  // Get distribution templates
  routes.push(['GET', '^/api/distribution/templates$', async (request) => {
    try {

      const { distributionPresets } = await getDistributionSystem();

      return createJSONResponse({
        templates: distributionPresets || {},
        timestamp: Date.now()
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to get templates: ${error.message}`, 500);
    }
  }]);

  return routes;
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}},{"ruleId":"no-process-exit","replacedBy":["n/no-process-exit"],"info":{"message":"Node.js rules were moved out of ESLint core.","url":"https://eslint.org/docs/latest/use/migrating-to-7.0.0#deprecate-node-rules","deprecatedSince":"7.0.0","availableUntil":null,"replacedBy":[{"message":"eslint-plugin-n now maintains deprecated Node.js-related rules.","plugin":{"name":"eslint-plugin-n","url":"https://github.com/eslint-community/eslint-plugin-n"},"rule":{"name":"no-process-exit","url":"https://github.com/eslint-community/eslint-plugin-n/tree/master/docs/rules/no-process-exit.md"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/services/api/routes/emotion.js","messages":[{"ruleId":"no-unused-vars","severity":2,"message":"'middlewareSystem' is defined but never used. Allowed unused args must match /^_/u.","line":10,"column"