        emotions: ['happy', 'sad', 'angry', 'fearful', 'disgusted', 'surprised', 'neutral'],
        model: 'CNN-7-emotion',
        version: '1.0',
        description: '7-emotion classification with valence-arousal mapping'
      }, 200);
    } catch (error) {

      return createErrorResponse(`Failed to get emotion labels: ${error.message}`, 500);
    }
  }]);

  return routes;
