 * Object factory registration and management for memory pool
 */

export const createFactoryManager = (state) => {

  // Register default object factories
  const registerDefaultFactories = () => {
    // Float32Array factory
    state.factories.set('Float32Array', (size) => new Float32Array(size));

    // Uint8Array factory
    state.factories.set('Uint8Array', (size) => new Uint8Array(size));

    // Canvas factory
    state.factories.set('Canvas', (width, height) => {
      if (typeof document !== 'undefined') {
        const canvas = document.createElement('canvas');
        canvas.width = width;
        canvas.height = height;
        return canvas;
      }
      return null;
    });

    // ImageData factory
    state.factories.set('ImageData', (width, height) => {
      if (typeof ImageData !== 'undefined') {
        return new ImageData(width, height);
      }
      // Fallback for Node.js or unsupported environments
      return {
        width,
        height,
        data: new Uint8ClampedArray(width * height * 4)
      };
    });

    // OffscreenCanvas factory (if available)
    if (typeof OffscreenCanvas !== 'undefined') {
      state.factories.set('OffscreenCanvas', (width, height) => 
        new OffscreenCanvas(width, height)
      );
    }

    // Generic object factory
    state.factories.set('Object', (template) => ({ ...template }));

    // Buffer factory
    state.factories.set('ArrayBuffer', (size) => new ArrayBuffer(size));
  };

  // Register custom factory
  const registerFactory = (type, factory) => {
    if (typeof factory !== 'function') {
      throw new Error(`Factory for type '${type}' must be a function`);
    }

    state.factories.set(type, factory);
    console.log(`ðŸ“¦ Registered factory for type: ${type}`);
  };

  // Get factory for type
  const getFactory = (type) => {
    return state.factories.get(type);
  };

  // Get all registered factories
  const getRegisteredTypes = () => {
    return Array.from(state.factories.keys());
  };

  return {
    registerDefaultFactories,
    registerFactory,
    getFactory,
    getRegisteredTypes
  };
};","usedDeprecatedRules":[{"ruleId":"max-len","replacedBy":["@stylistic/max-len"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"max-len","url":"https://eslint.style/rules/max-len"}}]}},{"ruleId":"no-mixed-operators","replacedBy":["@stylistic/no-mixed-operators"],"info":{"message":"Formatting rules are being moved out of ESLint core.","url":"https://eslint.org/blog/2023/10/deprecating-formatting-rules/","deprecatedSince":"8.53.0","availableUntil":"10.0.0","replacedBy":[{"message":"ESLint Stylistic now maintains deprecated stylistic core rules.","url":"https://eslint.style/guide/migration","plugin":{"name":"@stylistic/eslint-plugin","url":"https://eslint.style"},"rule":{"name":"no-mixed-operators","url":"https://eslint.style/rules/no-mixed-operators"}}]}}]},{"filePath":"/Users/Michael.Hildebrandt@ife.no/Claude/synopticon-api/src/shared/utils/memory-pool-stats.js","messages":[{"ruleId":"no-unused-vars","severity":2,"message":"'timeDelta' is assigned a value but never used. Allowed unused vars must match /^_/u.","line":70,"column":9,"nodeType":"Identifier","messageId":"unusedVar","endLine":70,"endColumn":18,"suggestions":[{"messageId":"removeVar","data":{"varName":"timeDelta"},"fix":{"range":[1821,1874],"text":""},"desc":"Remove unused variable 'timeDelta'."}]}],"suppressedMessages":[],"errorCount":1,"fatalErrorCount":0,"warningCount":0,"fixableErrorCount":0,"fixableWarningCount":0,"source":"/**
 * Memory Pool Statistics and Monitoring
 */

export const updatePoolStatistics = (stats, pools) => {
  // Update pool sizes
  stats.poolSizes = {};
  for (const [poolType, poolMap] of Object.entries(pools)) {
    stats.poolSizes[poolType] = {};
    for (const [key, pool] of poolMap) {
      stats.poolSizes[poolType][key] = pool.length;
    }
  }
};

export const recordAllocation = (stats) => {
  stats.allocations++;
};

export const recordDeallocation = (stats) => {
  stats.deallocations++;
};

export const recordReuseHit = (stats) => {
  stats.reuseHits++;
};

export const recordMemoryLeak = (stats) => {
  stats.memoryLeaks++;
};

export const calculateEfficiencyMetrics = (stats) => {
  const total = stats.allocations + stats.deallocations;
  const reuseRate = total > 0 ? stats.reuseHits / total : 0;
  const leakRate = stats.allocations > 0 ? stats.memoryLeaks / stats.allocations : 0;

  return {
    reuseRate: Math.round(reuseRate * 100) / 100,
    leakRate: Math.round(leakRate * 100) / 100,
    totalOperations: total,
    uptime: Date.now() - stats.created
  };
};

export const checkMemoryPressureLevel = (config) => {
  if (typeof performance === 'undefined' || !performance.memory) {
    return { level: 'unknown', usage: 0 };
  }

  const memoryInfo = performance.memory;
  const usedMB = memoryInfo.usedJSHeapSize / (1024 * 1024);

  let level = 'low';
  if (usedMB > config.maxMemoryPressureThreshold) {
    level = 'critical';
  } else if (usedMB > config.memoryPressureThreshold) {
    level = 'high';
  }

  return {
    level,
    usage: Math.round(usedMB),
    limit: memoryInfo.jsHeapSizeLimit / (1024 * 1024),
    total: memoryInfo.totalJSHeapSize / (1024 * 1024)
  };
};

export const updateAdaptiveState = (adaptiveState, cleanupEfficiency) => {
  const now = Date.now();
  const timeDelta = now - adaptiveState.lastUsageCheck;

  // Update cleanup efficiency
  adaptiveState.cleanupEfficiency = cleanupEfficiency;

  // Update usage history
  adaptiveState.usageHistory.push({
    timestamp: now,
    efficiency: cleanupEfficiency
  });

  // Keep only last 10 measurements
  if (adaptiveState.usageHistory.length > 10) {
    adaptiveState.usageHistory.shift();
  }

  // Calculate average efficiency
  const avgEfficiency = adaptiveState.usageHistory.reduce((sum, item) => sum + item.efficiency, 0) / adaptiveState.usageHistory.length;
  adaptiveState.averagePoolUsage = avgEfficiency;

  // Adjust cleanup interval based on efficiency
  if (avgEfficiency > 0.8) {
    // High efficiency - pools are being used well, cleanup less frequently
    adaptiveState.currentInterval = Math.min(adaptiveState.currentInterval * 1.2, 30000);
  } else if (avgEfficiency < 0.3) {
    // Low efficiency - pools are not being reused, cleanup more frequently
    adaptiveState.currentInterval = Math.max(adaptiveState.currentInterval * 0.8, 1000);
  }

  adaptiveState.lastUsageCheck = now;
};

export const getMemoryPoolSummary = (stats, pools, adaptiveState) => {
  const efficiency = calculateEfficiencyMetrics(stats);
  const memoryPressure = checkMemoryPressureLevel({ 
    memoryPressureThreshold: 100, 
    maxMemoryPressureThreshold: 150 
  });

  const totalPooledObjects = Object.values(pools).reduce((total, poolMap) => {
    return total + Array.from(poolMap.values()).reduce((sum, pool) => sum + pool.length, 0);
  }, 0);

  return {
    efficiency,
    memoryPressure,
    totalPooledObjects,
    adaptiveInterval: adaptiveState.currentInterval,
    averageEfficiency: adaptiveState.averagePoolUsage,
    stats: { ...stats },
